#' Univariate time series
#'
#' 
#' @param data timeseries data format
#' @param mode .
#' @param dep the dependent variable from \code{data}, variable must be
#'   numeric
#' @param dep1 the dependent variable from \code{data}, variable must be
#'   numeric
#' @param time1 ts type called 'ds'
#' @param freq .
#' @param pred .
#' @param plot .
#' @param box .
#' @param plot1 .
#' @param plot2 .
#' @param plot3 .
#' @param coef .
#' @param fit .
#' @param point .
#' @param plot4 .
#' @param plot5 .
#' @param plot6 .
#' @param periods .
#' @param unit .
#' @param resid .
#' @param plot9 .
#' @param plot10 .
#' @param level .
#' @param lbLag .
#' @param clean .
#' @param showAcc .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$progressBarHTML} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$coef} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$fit} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$point} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$accTrain} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$accCV} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$resid$diagTable} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$box} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot1} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot3} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot4} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot5} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot6} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot9} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot10} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$coef$asDF}
#'
#' \code{as.data.frame(results$coef)}
#'
#' @export
arima <- function(
    data,
    mode = "simple",
    dep,
    dep1,
    time1 = NULL,
    freq = 12,
    pred = 10,
    plot = FALSE,
    box = FALSE,
    plot1 = FALSE,
    plot2 = FALSE,
    plot3 = FALSE,
    coef = FALSE,
    fit = FALSE,
    point = FALSE,
    plot4 = FALSE,
    plot5 = FALSE,
    plot6 = FALSE,
    periods = 365,
    unit = "day",
    resid = FALSE,
    plot9 = FALSE,
    plot10 = FALSE,
    level = 95,
    lbLag = 0,
    clean = "FALSE",
    showAcc = FALSE) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("arima requires jmvcore to be installed (restart may be required)")

    if ( ! missing(dep)) dep <- jmvcore::resolveQuo(jmvcore::enquo(dep))
    if ( ! missing(dep1)) dep1 <- jmvcore::resolveQuo(jmvcore::enquo(dep1))
    if ( ! missing(time1)) time1 <- jmvcore::resolveQuo(jmvcore::enquo(time1))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(dep), dep, NULL),
            `if`( ! missing(dep1), dep1, NULL),
            `if`( ! missing(time1), time1, NULL))


    options <- arimaOptions$new(
        mode = mode,
        dep = dep,
        dep1 = dep1,
        time1 = time1,
        freq = freq,
        pred = pred,
        plot = plot,
        box = box,
        plot1 = plot1,
        plot2 = plot2,
        plot3 = plot3,
        coef = coef,
        fit = fit,
        point = point,
        plot4 = plot4,
        plot5 = plot5,
        plot6 = plot6,
        periods = periods,
        unit = unit,
        resid = resid,
        plot9 = plot9,
        plot10 = plot10,
        level = level,
        lbLag = lbLag,
        clean = clean,
        showAcc = showAcc)

    analysis <- arimaClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

