caretBase <- if (requireNamespace("jmvcore", quietly=TRUE)) R6::R6Class(
    "caretBase",
    inherit = jmvcore::Analysis,
    public = list(
        initialize = function(options, data=NULL, datasetId="", analysisId="", revision=0) {
            super$initialize(
                package = "snowCluster",
                name = "caret",
                version = c(1,0,0),
                options = options,
                results = caretResults$new(options=options),
                data = data,
                datasetId = datasetId,
                analysisId = analysisId,
                revision = revision,
                pause = NULL,
                completeWhenFilled = FALSE,
                requiresMissings = FALSE,
                weightsSupport = 'none')
        }))

#' Machine Learning
#'
#' 
#' @param data .
#' @param dep .
#' @param covs .
#' @param facs .
#' @param trans .
#' @param method .
#' @param cm1 .
#' @param mecon .
#' @param number .
#' @param repeats .
#' @param tune .
#' @param per .
#' @param over1 .
#' @param over .
#' @param cla .
#' @param cla1 .
#' @param tra .
#' @param tes .
#' @param plot .
#' @param plot1 .
#' @param plot2 .
#' @param plot3 Binary dependent required.
#' @param plot4 .
#' @param plot5 .
#' @param plot6 .
#' @param plot7 .
#' @param ml .
#' @param me .
#' @param num .
#' @param rep .
#' @param tune1 .
#' @param accu .
#' @param kapp .
#' @param width .
#' @param height .
#' @param width1 .
#' @param height1 .
#' @param width2 .
#' @param height2 .
#' @param width3 .
#' @param height3 .
#' @param width4 .
#' @param height4 .
#' @param width5 .
#' @param height5 .
#' @param width6 .
#' @param height6 .
#' @param width7 .
#' @param height7 .
#' @param plot8 .
#' @param width8 .
#' @param height8 .
#' @param positive .
#' @param positive1 .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$text1} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$over1} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$over} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$tra} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$tes} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$cla1} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$cla} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot8} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot4} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot1} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot3} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot5} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot6} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$mf$accu} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$mf$kapp} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot7} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$pred} \tab \tab \tab \tab \tab an output \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$over1$asDF}
#'
#' \code{as.data.frame(results$over1)}
#'
#' @export
caret <- function(
    data,
    dep,
    covs,
    facs,
    trans = "bagImpute",
    method = "pls",
    cm1 = "ctree",
    mecon = "cv",
    number = 10,
    repeats = 5,
    tune = 10,
    per = 0.7,
    over1 = TRUE,
    over = FALSE,
    cla = FALSE,
    cla1 = FALSE,
    tra = TRUE,
    tes = FALSE,
    plot = FALSE,
    plot1 = FALSE,
    plot2 = FALSE,
    plot3 = FALSE,
    plot4 = FALSE,
    plot5 = FALSE,
    plot6 = FALSE,
    plot7 = FALSE,
    ml = "pls,ctree,knn,rf",
    me = "cv",
    num = 10,
    rep = 5,
    tune1 = 10,
    accu = FALSE,
    kapp = FALSE,
    width = 500,
    height = 500,
    width1 = 500,
    height1 = 500,
    width2 = 500,
    height2 = 500,
    width3 = 500,
    height3 = 500,
    width4 = 500,
    height4 = 500,
    width5 = 500,
    height5 = 500,
    width6 = 500,
    height6 = 500,
    width7 = 500,
    height7 = 500,
    plot8 = FALSE,
    width8 = 500,
    height8 = 500,
    positive = "no",
    positive1 = "no") {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("caret requires jmvcore to be installed (restart may be required)")

    if ( ! missing(dep)) dep <- jmvcore::resolveQuo(jmvcore::enquo(dep))
    if ( ! missing(covs)) covs <- jmvcore::resolveQuo(jmvcore::enquo(covs))
    if ( ! missing(facs)) facs <- jmvcore::resolveQuo(jmvcore::enquo(facs))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(dep), dep, NULL),
            `if`( ! missing(covs), covs, NULL),
            `if`( ! missing(facs), facs, NULL))

    for (v in dep) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])
    for (v in facs) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- caretOptions$new(
        dep = dep,
        covs = covs,
        facs = facs,
        trans = trans,
        method = method,
        cm1 = cm1,
        mecon = mecon,
        number = number,
        repeats = repeats,
        tune = tune,
        per = per,
        over1 = over1,
        over = over,
        cla = cla,
        cla1 = cla1,
        tra = tra,
        tes = tes,
        plot = plot,
        plot1 = plot1,
        plot2 = plot2,
        plot3 = plot3,
        plot4 = plot4,
        plot5 = plot5,
        plot6 = plot6,
        plot7 = plot7,
        ml = ml,
        me = me,
        num = num,
        rep = rep,
        tune1 = tune1,
        accu = accu,
        kapp = kapp,
        width = width,
        height = height,
        width1 = width1,
        height1 = height1,
        width2 = width2,
        height2 = height2,
        width3 = width3,
        height3 = height3,
        width4 = width4,
        height4 = height4,
        width5 = width5,
        height5 = height5,
        width6 = width6,
        height6 = height6,
        width7 = width7,
        height7 = height7,
        plot8 = plot8,
        width8 = width8,
        height8 = height8,
        positive = positive,
        positive1 = positive1)

    analysis <- caretClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

