#' Hierarchical Clustering
#'
#' 
#' @param data .
#' @param podatki .
#' @param imena .
#' @param stand .
#' @param dis .
#' @param method .
#' @param grp .
#' @param group .
#' @param hght .
#' @param case .
#' @param dend .
#' @param horiz .
#' @param heat .
#' @param pair .
#' @param width .
#' @param height .
#' @param width1 .
#' @param height1 .
#' @param width2 .
#' @param height2 .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$izpis} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$groups} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$heat} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$pairs} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$clust} \tab \tab \tab \tab \tab an output \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$izpis$asDF}
#'
#' \code{as.data.frame(results$izpis)}
#'
#' @export
hcm <- function(
    data,
    podatki,
    imena,
    stand = TRUE,
    dis = "euclidean",
    method = "ward.D2",
    grp = "number",
    group = 2,
    hght = 15,
    case = FALSE,
    dend = FALSE,
    horiz = FALSE,
    heat = FALSE,
    pair = FALSE,
    width = 500,
    height = 500,
    width1 = 500,
    height1 = 500,
    width2 = 500,
    height2 = 500) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("hcm requires jmvcore to be installed (restart may be required)")

    if ( ! missing(podatki)) podatki <- jmvcore::resolveQuo(jmvcore::enquo(podatki))
    if ( ! missing(imena)) imena <- jmvcore::resolveQuo(jmvcore::enquo(imena))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(podatki), podatki, NULL),
            `if`( ! missing(imena), imena, NULL))


    options <- hcmOptions$new(
        podatki = podatki,
        imena = imena,
        stand = stand,
        dis = dis,
        method = method,
        grp = grp,
        group = group,
        hght = hght,
        case = case,
        dend = dend,
        horiz = horiz,
        heat = heat,
        pair = pair,
        width = width,
        height = height,
        width1 = width1,
        height1 = height1,
        width2 = width2,
        height2 = height2)

    analysis <- hcmClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

