#' K-means Clustering
#'
#' 
#' @param data The data as a data frame.
#' @param vars .
#' @param factors .
#' @param k .
#' @param k1 .
#' @param kp .
#' @param oc .
#' @param algo .
#' @param nstart .
#' @param stand .
#' @param plot .
#' @param angle a number from 0 to 90 defining the angle of the x-axis labels,
#'   where 0 degrees represents completely horizontal labels.
#' @param plot1 .
#' @param plot2 .
#' @param plot3 .
#' @param width .
#' @param height .
#' @param width1 .
#' @param height1 .
#' @param width2 .
#' @param height2 .
#' @param width3 .
#' @param height3 .
#' @param plot4 .
#' @param width4 .
#' @param height4 .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$ss} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$clustering} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$centroids} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot1} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot3} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$clust} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$clust1} \tab \tab \tab \tab \tab an output \cr
#'   \code{results$oc} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$kp} \tab \tab \tab \tab \tab a table \cr
#'   \code{results$plot4} \tab \tab \tab \tab \tab an image \cr
#' }
#'
#' Tables can be converted to data frames with \code{asDF} or \code{\link{as.data.frame}}. For example:
#'
#' \code{results$ss$asDF}
#'
#' \code{as.data.frame(results$ss)}
#'
#' @export
kmeans <- function(
    data,
    vars,
    factors,
    k = 2,
    k1 = 2,
    kp = FALSE,
    oc = FALSE,
    algo = "Hartigan-Wong",
    nstart = 10,
    stand = FALSE,
    plot = FALSE,
    angle = 0,
    plot1 = FALSE,
    plot2 = FALSE,
    plot3 = FALSE,
    width = 500,
    height = 500,
    width1 = 500,
    height1 = 500,
    width2 = 500,
    height2 = 500,
    width3 = 500,
    height3 = 500,
    plot4 = FALSE,
    width4 = 500,
    height4 = 500) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("kmeans requires jmvcore to be installed (restart may be required)")

    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if ( ! missing(factors)) factors <- jmvcore::resolveQuo(jmvcore::enquo(factors))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(vars), vars, NULL),
            `if`( ! missing(factors), factors, NULL))

    for (v in factors) if (v %in% names(data)) data[[v]] <- as.factor(data[[v]])

    options <- kmeansOptions$new(
        vars = vars,
        factors = factors,
        k = k,
        k1 = k1,
        kp = kp,
        oc = oc,
        algo = algo,
        nstart = nstart,
        stand = stand,
        plot = plot,
        angle = angle,
        plot1 = plot1,
        plot2 = plot2,
        plot3 = plot3,
        width = width,
        height = height,
        width1 = width1,
        height1 = height1,
        width2 = width2,
        height2 = height2,
        width3 = width3,
        height3 = height3,
        plot4 = plot4,
        width4 = width4,
        height4 = height4)

    analysis <- kmeansClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

