#' Multidimensional Scaling Plot
#'
#' 
#' @param mode .
#' @param data The data as a data frame.
#' @param labels .
#' @param vars .
#' @param k .
#' @param plot .
#' @param plot1 .
#' @param xlab .
#' @param ylab .
#' @param zlab .
#' @param plot2 .
#' @param width .
#' @param height .
#' @param width1 .
#' @param height1 .
#' @param width2 .
#' @param height2 .
#' @return A results object containing:
#' \tabular{llllll}{
#'   \code{results$instructions} \tab \tab \tab \tab \tab a html \cr
#'   \code{results$plot} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot1} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$plot2} \tab \tab \tab \tab \tab an image \cr
#'   \code{results$text} \tab \tab \tab \tab \tab a preformatted \cr
#'   \code{results$clust} \tab \tab \tab \tab \tab an output \cr
#' }
#'
#' @export
mds <- function(
    mode = "simple",
    data,
    labels,
    vars,
    k = 2,
    plot = FALSE,
    plot1 = FALSE,
    xlab,
    ylab,
    zlab,
    plot2 = FALSE,
    width = 500,
    height = 500,
    width1 = 500,
    height1 = 500,
    width2 = 500,
    height2 = 500) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("mds requires jmvcore to be installed (restart may be required)")

    if ( ! missing(labels)) labels <- jmvcore::resolveQuo(jmvcore::enquo(labels))
    if ( ! missing(vars)) vars <- jmvcore::resolveQuo(jmvcore::enquo(vars))
    if ( ! missing(xlab)) xlab <- jmvcore::resolveQuo(jmvcore::enquo(xlab))
    if ( ! missing(ylab)) ylab <- jmvcore::resolveQuo(jmvcore::enquo(ylab))
    if ( ! missing(zlab)) zlab <- jmvcore::resolveQuo(jmvcore::enquo(zlab))
    if (missing(data))
        data <- jmvcore::marshalData(
            parent.frame(),
            `if`( ! missing(labels), labels, NULL),
            `if`( ! missing(vars), vars, NULL),
            `if`( ! missing(xlab), xlab, NULL),
            `if`( ! missing(ylab), ylab, NULL),
            `if`( ! missing(zlab), zlab, NULL))


    options <- mdsOptions$new(
        mode = mode,
        labels = labels,
        vars = vars,
        k = k,
        plot = plot,
        plot1 = plot1,
        xlab = xlab,
        ylab = ylab,
        zlab = zlab,
        plot2 = plot2,
        width = width,
        height = height,
        width1 = width1,
        height1 = height1,
        width2 = width2,
        height2 = height2)

    analysis <- mdsClass$new(
        options = options,
        data = data)

    analysis$run()

    analysis$results
}

